import React, { Component } from 'react';
import './App.css';
import axios from 'axios';

class App extends Component {

  state = {
    employees: [],
    newEmployeeData: {
      name: '',
      salary: '',
      age: ''
    },
    editEmployeeData: {
      id: '',
      name: '',
      salary: '',
      age: ''
    },
    newEmployeeModal: false,
    editEmployeeModal: false
  }
  componentWillMount() {
    this._refreshEmployees();
  }
  toggleNewEmployeeModal() {
    this.setState({
      newEmployeeModal: ! this.state.newEmployeeModal
    });
  }
  toggleEditEmployeeModal() {
    this.setState({
      editEmployeeModal: ! this.state.editEmployeeModal
    });
  }
  addEmployee() {
    axios.post('http://localhost:3000/employees', this.state.newEmployeeData).then((response) => {
      let { employees } = this.state;

      employees.push(response.data);

      this.setState({ employees, newEmployeeModal: false, newEmployeeData: {
        name: '',
        salary: '',
        age: ''
      }});
    });
  }
  updateEmployee() {
    let { name, salary, age } = this.state.editEmployeeData;

    axios.put('http://localhost:3000/employees/' + this.state.editEmployeeData.id, {
      name, salary, age
    }).then((response) => {
      this._refreshEmployees();

      this.setState({
        editEmployeeModal: false, editEmployeeData: { id: '', name: '', salary: '', age: '' }
      })
    });
  }
  editEmployee(id, name, salary, age) {
    this.setState({
      editEmployeeData: { id , name , salary, age }, editEmployeeModal: ! this.state.editEMployeeModal
    });
  }
  deleteEmployee(id) {
    axios.delete('http://localhost:3000/employees/' + id).then((response) => {
      this._refreshEmployee();
    });
  }
  __refreshEmployees() {
    axios.get('http://localhost:3000/employees').then((response) => {
      this.setState({
        employees: response.data
      })
    });
  }
  render() {
    let employees = this.state.employees.map((employee) => {
      return (
        <tr key={employee.id}>
          <td>{employee.id}</td>
          <td>{employee.name}</td>
          <td>{employee.salary}</td>
          <td>{employee.age}</td>
          <td>
            <button className="mr-2" onClick={this.editEmployee.bind(this, employee.id, employee.name, employee.salary, employee.age)}>Edit</button>
            <button onClick={this.deleteEmployee.bind(this, employee.id)}>Delete</button>
          </td>
        </tr>
      )
    });

  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     title: "CRUD Sample Application",
  //     act: 0,
  //     index: '',
  //     datas:[]
  //   }
  // }

  // componentDidMount() {
