  // }
  }
  render() {
    let employees = this.state.employees.map((employee) => {
      return (
        <tr key={employee.id}>
          <td>{employee.id}</td>
          <td>{employee.name}</td>
          <td>{employee.salary}</td>
          <td>{employee.age}</td>
          <td>
            <button color="success" size="sm" className="mr-2" onClick={this.editEmployee.bind(this, employee.id, employee.name, employee.salaryy, employee.age)}>EDIT</button>
            <button color="danger" size="sm" onClick={this.deleteEmployee.bind(this, employee.id)}>DELETE</button>
          </td>
        </tr>
      )
    });
    return (
      <div className="App container">

      <h1>Crud App</h1>

      <button className="my-3" color="primary" onClick={this.toggleNewEmployeeModal.bind(this)}>ADD</button>

      <modal isOpen={this.state.newEmployeeModal} toggle={this.toggleNewEmployeeModal.bind(this)}>
        <header toggle={this.toggleNewEmployeeModal.bind(this)}>Add New Employee</header>
        <body>
          <form>
            <label for="name">Name</label>
            <input id="name" value={this.state.newEmployeeData.name} onChange={(e) => {
              let { newEmployeeData } = this.state;

              newEmployeeData.name = e.target.value;

              this.setState({ newEmployeeData });
            }} />
          </form>
          <form>
            <label for="salary">Salary</label>
            <input id="salary" value={this.state.newEmployeeData.salary} onChange={(e) => {
              let { newEmployeeData } = this.state;

              newEmployeeData.salary = e.target.value;

              this.setState({ newEmployeeData });
